import React, {useContext} from "react"
import Context from './context'

const styles = {
    li: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '1rem 1rem',
        border: '0px solid',
        borderRadius: '15px',
        background: '#fefcff',
        boxShadow: '-10px 10px 19px #c4c2c4, 10px -10px 19px #ffffff',   
        marginBottom: '1.5rem',
    },

    input: {
        marginTop: '0',
        marginRight: '1rem',
    },

    span: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },

    button: {
        outline: 'none',
    },
};

export default function TodoItem({ todo, index, onChange}) {
    const { removeTodo } = useContext(Context);
    const classes = [];

    if(todo.completed) {
        classes.push('done');
    };

    return (
    <li style={styles.li}>
        <span className={classes.join(' ')} style={styles.span}>
            <input type='checkbox' checked={todo.completed} style={styles.input} onChange={() => onChange(todo.id)}/>
            <strong>{ index + 1 }</strong>
            &nbsp; - &nbsp;
            { todo.title }  
        </span>

        <button className='rm' onClick={() => {removeTodo(todo.id)}}>&times;</button>
    </li>
    )
}
