import React from "react"

const styles = {
    body__filter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },

    body__button: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '25px',
        border: '0',
        outline: '0',
        fontSize: '12px',
        padding: '10px',
        borderRadius: '12px',
        backgroundColor: '#EBECF0',
        textShadow: '1px 1px 0 #ffffff',
        width: '15%',
        boxSizing: 'border-box',
        appearance: 'none',
        color: '#61677C',
        transition: 'all 0.2s ease-in-out',
        cursor: 'pointer',
        fontWeight: '600',
    },
};

export default function TodoFilter(props) {
    let value = 1;

    return (
        <div style={styles.body__filter}>
            <button 
                style={styles.body__button}
                id='allButton'
                className='body__buttonFil'
                onClick={(event) => {
                    if(value != 1) {
                        value = 1;
                        event.target.className = 'body__buttonFilAct';
                    };
                    event.target.className = 'body__buttonFil';
                }}
                >All
            </button>
            <button 
                style={styles.body__button}
                id='activeButton'
                className='body__buttonFil'
                onClick={(event) => {
                    if(value != 2) {
                        value = 2;
                        event.target.className = 'body__buttonFilAct';
                    };
                    event.target.className = 'body__buttonFil';
                }}
                >Active
            </button>
            <button 
                style={styles.body__button}
                id='completedButton'
                className='body__buttonFil'
                onClick={(event) => {
                    if(value != 3) {
                        value = 3;
                        event.target.className = 'body__buttonFilAct';
                    };
                    event.target.className = 'body__buttonFil';
                }}
                >Completed
            </button>
        </div>
    );
}
