import React from 'react';
import TodoList from './Todo/TodoList';
import Context from './context';
import AddTodo from './Todo/AddTodo';

function App() {
  const [todoList, setTodoList] = React.useState([
    { id: 1, completed: false, title: 'buy apples' },
    { id: 2, completed: false, title: 'buy bananas' },
    { id: 3, completed: false, title: 'buy paper' },
  ])

  function toggleTodo(id) {
    setTodoList(
      todoList.map(todo => {
        if(todo.id === id) {
          todo.completed = !todo.completed;
        };
        return todo;
      })
    )
  };

  function removeTodo(id) {
    setTodoList(todoList.filter(todo => todo.id !== id));
  };

  function addTodo(title) {
    setTodoList(todoList.concat([{
      title,
      id: Date.now(),
      completed: false,
    }]));
  };

  function valComp() {
    setTodoList(todoList.map(todo => {
        let i = 0;
        if(todo.completed === true) {
          ++i;
        };
        return i;
      })
    );
  };

  return (
    <Context.Provider value={{ removeTodo }}> 
      <div className='wrapper'>
        <h1>To-Do List</h1>
        <AddTodo onCreate={addTodo}/>
        <p>Total:{setTodoList(todoList.length)} Completed:{}</p>
        {todoList.length
          ? <TodoList todo={todoList} onToggle={toggleTodo}/> 
          : <p align='center'>no todos</p>
        }
      </div>
    </Context.Provider> 
  );
};

export default App;
