import React from 'react';
import TodoList from './Todo/TodoList';
import AddTodo from './Todo/AddTodo';
import TodoFilter from './Todo/TodoFilter';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: [
        { id: 1, completed: false, title: 'buy apples' },
        { id: 2, completed: false, title: 'buy bananas' },
        { id: 3, completed: false, title: 'buy paper' },
      ],
      filter: 1,
    };

    this.toggleTodo = this.toggleTodo.bind(this);
    this.removeTodo = this.removeTodo.bind(this);
    this.addTodo = this.addTodo.bind(this);
  }

  styles = {
    body: {
      padding: "1rem 3rem",
      margin: "0 auto",
      width: "600px",
    },

    body__title: {
      color: '#BABECC',
      textShadow: '1px 1px 1px #ffffff',
      display: 'flex',
      justifyContent: 'center',
      fontSize: '40px',
    },

    body__text: {
      textAlign: 'center',
      marginBottom: '20px',
    },
  }

  toggleTodo(id) {
    const newTodos = this.state.todoList.map(todo => {
      if(todo.id === id) {
        todo.completed = !todo.completed;
      };
      return todo;
    });

    this.setState({
      todoList: newTodos
    });
  }

  removeTodo(id) {
    this.setState({
      todoList: this.state.todoList.filter(todo => todo.id !== id)
    });
  }

  addTodo(title) {
    this.setState({
      todoList: this.state.todoList.concat([{
        title,
        id: Date.now(),
        completed: false,
      }])
    });
  }

  valComp() {
    let val = 0;
    for(let i = 0; i < this.state.todoList.length; i++) {
      if (this.state.todoList[i].completed === true) ++val;
    };
    return val;
  }

  render() {
    return (
      <div style={this.styles.body}>
        <h1 style={this.styles.body__title}>To-Do List</h1>
        <AddTodo onCreate={this.addTodo}/>
        <p style={this.styles.body__text}>
          Total: {this.state.todoList.length} &nbsp; &nbsp; Completed: {this.valComp()}
        </p>
        {this.state.todoList.length
          ? <TodoList
            todo={this.state.todoList}
            onToggle={this.toggleTodo}
            removeTodo={this.removeTodo}
            /> 
          : <p style={this.styles.body__text}>no todos</p>
        }
        <TodoFilter todo={this.state.todoList} filter={this.state.filter}/>
      </div>
    );
  }
};

export default App;
