import React from "react"

const styles = {
    body__filter: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },

    body__button: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '25px',
        border: '0',
        outline: '0',
        fontSize: '12px',
        padding: '10px',
        borderRadius: '12px',
        backgroundColor: '#EBECF0',
        textShadow: '1px 1px 0 #ffffff',
        width: '15%',
        boxSizing: 'border-box',
        appearance: 'none',
        color: '#61677C',
        transition: 'all 0.2s ease-in-out',
        cursor: 'pointer',
        fontWeight: '600',
    },
};

export default function TodoFilter(props) {
    let value = 1;

    function checkFilter(value) {
        switch(value) {
            case 1:
                document.querySelector('#allButton').className = 'body__buttonFilAct';
                document.querySelector('#activeButton').className = 'body__buttonFil';
                document.querySelector('#completedButton').className = 'body__buttonFil';
                break;
            case 2:
                document.querySelector('#allButton').className = 'body__buttonFil';
                document.querySelector('#activeButton').className = 'body__buttonFilAct';
                document.querySelector('#completedButton').className = 'body__buttonFil';
                break;
            case 3:
                document.querySelector('#allButton').className = 'body__buttonFil';
                document.querySelector('#activeButton').className = 'body__buttonFil';
                document.querySelector('#completedButton').className = 'body__buttonFilAct';
                break;
        }
    };

    return (
        <div style={styles.body__filter}>
            <button 
                style={styles.body__button}
                id='allButton'
                className='body__buttonFil'
                onClick={(event) => {checkFilter(1)}}
                >All
            </button>
            <button 
                style={styles.body__button}
                id='activeButton'
                className='body__buttonFil'
                onClick={(event) => {checkFilter(2)}}
                >Active
            </button>
            <button 
                style={styles.body__button}
                id='completedButton'
                className='body__buttonFil'
                onClick={(event) => {checkFilter(3)}}
                >Completed
            </button>
        </div>
    );
}
